import React, {Fragment, useState} from 'react';


const Form = (props) => {
	const estadoInicial = {
		nome: '',
		descricao: '',
		link: '',
		img_url: '',
		id: ''
	}

	let [campos, setCampos] = useState(estadoInicial);

	const handleCampoChange = e => setCampos({
		...campos, 
		[e.currentTarget.nome]: e.currentTarget.value
	});

	const handleSubmit = event => {
		props.adicionarPlaneta(campos);
		event.preventDefault();


	}


	return(
		<Fragment>
			<form onSubmit={handleSubmit}>
				<div>
					<label htmlFor="name">Nome:  </label>
					<input id="nome" name="name" type="text" onChange={handleCampoChange} value={campos.nome}/>
				</div>
				<br/>
				<div>
					<input type="submit" value="enviar" />
				</div>
			</form>
		</Fragment>
	)
} 

export default Form;
