import React, {Fragment, useState} from 'react';


const Form = (props) => {

	let [campos, setCampos] = useState();
	const handleCampoChange = e => setCampos({
		...campos, 
		[e.currentTarget.name]: e.cuurentTarget.value
	});

	const handleSubmit = event => {
		props.adicionarPlaneta(campos);
		event.preventDefault();


	}


	return(
		<Fragment>
			<form onSubmit={handleSubmit}>
				<div>
					<label htmlFor="nome">Nome:  </label>
					<input id="nome" name="name" type="text" onChange={handleCampoChange} value={campos.name}/>
				</div>
				<br/>
				<div>
					<input type="submit" value="enviar" />
				</div>
			</form>
		</Fragment>
	)
} 

export default Form;
