import React, {Fragment, useState} from 'react';

const Form = (props) => {
	const [nameSatelite, setNameSatelite] = useState('');

	const handleChange = event => setNameSatelite(event.target.value);

	const handleSubmit = event => {
		props.adicionarSatelites({name: nameSatelite});
		event.preventDefault();
	}

	return(
			<Fragment>
				<form onSubmit={handleSubmit} >
					<div>
						<label htmlFor="name">Nome:  </label>
						<input id="name" name="name" type="text" onChange={handleChange} value={nameSatelite}/>
					</div>
					<br/>
					<div>
						<input type="submit" value="Satelite" />
					</div>
				</form>
			</Fragment>
	)


} 

export default Form;
