import React, {Fragment} from 'react';
import Planeta from './planeta';

async function getPlanetas(){
	const response = await fetch('http://localhost:3000/api/planets.json');
	const result = await response.json();
	return result;
}


class Planetas extends React.Components {

	constructor(props){
		super(props);
		this.state = {
			planetas: []
		}
	}

	componentDidMount() {
		getPlanetas().then((item) => {
			this.setState((state) => {
				planetas: item["planets"]
			})
		})
	}

	render(){
				return(	
			<div>
				<h3> Lista de Planetas </h3>
			</div>
			)
	}
}

export default Planetas;
